public List<Integer> doubleNumbers(List<Integer> numbers) {
    return numbers.stream()
        .map(n -> n * 2)
        .collect(Collectors.toList());
}

public List<String> filterLongNames(List<String> names) {
    return names.stream()
        .filter(name -> name.length() > 5)
        .collect(Collectors.toList());
}

public Map<String, Integer> createNameLengthMap(List<String> names) {
    return names.stream()
        .collect(Collectors.toMap(
            name -> name,
            name -> name.length()
        ));
}

public int sumNumbers(List<Integer> numbers) {
    return numbers.stream()
        .reduce(0, Integer::sum);
}

public Map<Integer, List<Person>> groupByAge(List<Person> people) {
    return people.stream()
        .collect(Collectors.groupingBy(Person::getAge));
}
