public void processData(Data data) throws ValidationException {
    if (data == null) {
        throw new IllegalArgumentException("Data cannot be null");
    }

    try {
        validate(data);
        transform(data);
        save(data);
    } catch (ValidationException e) {
        logger.error("Validation failed: {}", e.getMessage());
        throw e;
    } catch (IOException e) {
        logger.error("IO error during processing", e);
        throw new ProcessingException("Failed to save data", e);
    }
}

public String readFile(Path filePath) throws IOException {
    try (BufferedReader reader = Files.newBufferedReader(filePath, StandardCharsets.UTF_8)) {
        return reader.lines()
            .collect(Collectors.joining("\n"));
    }
}

public void updateRecord(String id, Map<String, Object> updates) {
    try {
        Record record = repository.findById(id)
            .orElseThrow(() -> new RecordNotFoundException("Record not found: " + id));

        record.update(updates);
        repository.save(record);

    } catch (RecordNotFoundException e) {
        logger.warn("Record not found: {}", id);
        throw e;
    } catch (ValidationException e) {
        logger.error("Invalid updates: {}", e.getMessage());
        throw new IllegalArgumentException("Invalid updates", e);
    } catch (Exception e) {
        logger.error("Unexpected error updating record", e);
        throw new RuntimeException("Failed to update record", e);
    }
}
